import UIKit

protocol ListPresentationLogic {
    func presentFetchedGroups(response: List.FetchGroups.Response)
}

class ListPresenter: ListPresentationLogic {
    weak var viewController: ListDisplayLogic?

    // MARK: Present Fetched Groups

    func presentFetchedGroups(response: List.FetchGroups.Response) {
        var displayedGroups: [List.FetchGroups.ViewModel.DisplayedGroup] = []
        for group in response.groups {
            let displayGroup = List.FetchGroups.ViewModel.DisplayedGroup(
                name: group.name,
                city: group.city
            )
            displayedGroups.append(displayGroup)
        }
        let viewModel = List.FetchGroups.ViewModel(displayedGroups: displayedGroups)
        viewController?.displayGroups(viewModel: viewModel)
    }
}
